(function($) {
    $.getParam = function(key) {
        var urlParams = decodeURI(window.location.search.substring(1));

        if (!urlParams || urlParams === "") {
            return null;
        }

        var pairs = urlParams.split("&");

        var keyValue_Collection = {};
        for (var i = 0; i < pairs.length; i++) {
            var value = pairs[i];

            var equalsignPosition = value.indexOf("=");

            if (equalsignPosition === -1) {
                keyValue_Collection[value] = true;
            } else {
                keyValue_Collection[value.substring(0, equalsignPosition)] = value.substr(equalsignPosition + 1);
            }
        }
        if (key) {
            return keyValue_Collection[key];
        }
        return keyValue_Collection;
    }
})(jQuery);
